<script> var baseUrl="{{$url}}",formElement=document.getElementById("callburn-form"),telElement=formElement["callburn-number"],submitElement=formElement["callburn-submit"],data=new Object;telElement.addEventListener("change",function(){data.recipient=telElement.value}),submitElement.addEventListener("click",function(){if(data.recipient){var e=new Date;data.offset=e.getTimezoneOffset()/60*-1,data.full_date=e.getDate()+"/"+(1===String(e.getMonth()).length?"0"+e.getMonth():e.getMonth())+"/"+e.getFullYear(),data.token="{{$token}}",data.status="start";var t=new XMLHttpRequest;t.onreadystatechange=function(){if(4==this.readyState&&200==this.status){var e=JSON.parse(t.responseText);0===e.resource.error.no&&console.info("Call in process...")}else 405==this.status&&alert("This domain is not authorized. Add it on callburn.com to give the ability to this snippet to work properly")},t.open("POST",baseUrl+"/ctc/create-message"),t.setRequestHeader("Content-Type","application/json"),t.send(JSON.stringify(data))}}); </script>